cid = #{item.cid}
AND crgid = #{item.crgid}
AND ptype = #{item.ptype}
AND pid = #{item.pid}
AND mid = #{item.mid}
AND pageid=#{item.pageid}

mapper.update:
UPDATE tb_role_menu_auth
SET status =#{status},
updtime =#{updtime},
ouid = #{ouid}
WHERE
cid = #{cid}
AND crgid = #{crgid}
AND ptype = #{ptype}
AND pid = #{pid}
AND mid = #{mid}
AND pageid=#{pageid}

choose.when:
UPDATE tb_role_control_auth
SET status =#{status},
updtime =#{updtime},
ouid =#{ouid}
WHERE
cid =#{cid}
AND crgid =#{crgid}
AND pageid =#{pageid}

    AND controlid=#{controlid}

choose.otherwise:
UPDATE tb_role_control_auth
SET status =#{status},
updtime =#{updtime},
ouid =#{ouid}
FROM
tb_role_menu_auth ma
WHERE
tb_role_control_auth.cid = ma.cid
AND tb_role_control_auth.crgid = ma.crgid
AND tb_role_control_auth.pageid = ma.pageid

AND ma.cid =#{cid}
AND ma.crgid =#{crgid}
AND ma.pid =#{pid}
AND ma.mid =#{mid}

    AND tb_role_control_auth.controlid =#{controlid}

AND ma.ptype= #{ptype}

choose.when:
