tb_role_control_auth.cid = ma.cid
AND tb_role_control_auth.crgid = ma.crgid
AND tb_role_control_auth.pageid = ma.pageid

AND ma.cid =#{item.cid}
AND ma.crgid =#{item.crgid}
AND ma.pid =#{item.pid}
AND ma.mid =#{item.mid}

    AND tb_role_control_auth.controlid =#{item.controlid}

AND ma.ptype= #{item.ptype}

mapper.select:
SELECT
    count(1)
FROM
    tb_role_menu_auth
WHERE
    crgid = #{crgid}
    AND pid =#{pid}
    and ptype=#{ptype}
    and cid=#{cid}

        SELECT
	count(1)
        FROM
            tb_role_relation
        WHERE
         rrid = #{rrid}
         AND
         rrtype = #{rrtype}
         AND
        crgid = #{crgid}
