mapper.select:
select cid,groupid,groupname,msgtype,business from tb_message_group where status = '1'
and groupname = #{groupname}
and groupid = #{groupid}
and msgtype = #{msgtype}
limit #{rows}  offset #{page}

select * from tb_msg_user_group where status = '1'
and cid = #{cid}
and groupid = #{groupid}
and uid = #{uid}
and msgtype = #{msgtype}
limit #{rows}  offset #{page}

select * from tb_msg_group_configure where status = '1'
and groupid = #{groupid}
and msgtype = #{msgtype}
limit #{rows}  offset #{page}

select count(1) from tb_message_group where status = '1'
and groupname = #{groupname}
and groupid = #{groupid}
and msgtype = #{msgtype}

select count(1) from tb_msg_user_group where status = '1'
and cid = #{cid}
and groupid = #{groupid}
and uid = #{uid}
and msgtype = #{msgtype}

select cid,groupid,groupname,msgtype,business from tb_message_group where status = '1'
or groupname like CONCAT('%',#{keys},'%')
limit  #{rows}  offset #{page}

select * from tb_msg_user_group where status = '1'
and nickname like CONCAT('%',#{nickname},'%')
limit #{rows}  offset #{page}

mapper.update:
update tb_message_group set  updtime = #{updtime}
, upduid = #{upduid}
, groupname = #{groupname}
, msgtype = #{msgtype}
where cid = #{cid} and groupid = #{groupid}

update tb_msg_user_group set ouid = #{ouid} , updtime = #{updtime}
, cid = #{cid}
, groupid = #{groupid}
, msgtype = #{msgtype}
, uid = #{uid}
, nickname = #{nickname}
, tarobj = #{tarobj}
where id = #{id}

update tb_msg_group_configure set ouid = #{ouid} , updtime = #{updtime}
, cid = #{cid}
, groupid = #{groupid}
, msgtype = #{msgtype}
, context = #{context}
, passwd = #{passwd}
, summary = #{summary}
where id = #{id}

update tb_message_group set status = '2' , updtime = #{updtime}
, upduid = #{upduid}
where cid = #{cid} and groupid = #{groupid}
