mapper.insert:
INSERT INTO tb_message_type ( msgtype, typename, typedesc, addtime, ouid, updtime, status, summary ) VALUES ( #{msgtype}, #{typename}, #{typedesc}, #{addtime}, #{ouid}, #{updtime}, #{status},
#{summary} null
);

mapper.update:
update tb_message_type set status = '1'

    ,msgtype = #{msgtype}


    ,typename = #{typename}


    ,typedesc = #{typedesc}


    ,addtime = #{addtime}


    ,ouid = #{ouid}


    ,updtime = #{updtime}


    ,summary = #{summary}

where id = #{id}
