mapper.select:
SELECT otid as apitype, cname as name from tb_open_api_type
otid = #{apitype}
and status = #{status}
and status = '1'
order by otid limit #{pagesize} offset #{pageno}

SELECT apiid, cname, protocol, reqtype, curl from tb_open_api_list
otid = #{apitype}
and status = #{status}
and status = '1'
order by idx limit #{pagesize} offset #{pageno}

SELECT helpid, htitle as title from tb_platform_helper
and status = #{status}
and status = '1'
order by id limit #{pagesize} offset #{pageno}

SELECT hcontent as content from tb_platform_helper
helpid = #{helpid}
and status = #{status}
and status = '1'

mapper.insert:
INSERT INTO tb_platform_org (cid, manager, ouid, addtime, status, summary) VALUES ( #{cid}, #{uid}, #{uid}, #{addtime},
#{status},
'1',
#{summary}
null
)

mapper.select:
select appid, appline, appkey, appsecret, devtype, apptype, cid, keys, appname, applogo, appdesc, appurl, appips, manurl, summary from tb_org_application where cid = #{cid}
and apptype = #{apptype}
and devtype = #{devtype}
order by id limit #{pagesize} offset #{pageno}

mapper.insert:
INSERT INTO tb_app_developer (appid, uid, ouid, devauth, addtime, updtime, status, summary) VALUES ( #{appid}, #{uid}, #{userId}, #{devauth}, #{addtime}, #{updtime},
#{status},
'1',
#{summary}
null
)

mapper.update:
UPDATE
tb_app_developer
SET
status = '2',
updtime = #{updtime},
ouid = #{uid}
WHERE
appid = #{appid}
and uid = #{userId}

UPDATE
tb_app_developer
SET
status = '1',
devauth = #{devauth},
updtime = #{updtime},
ouid = #{userId}
WHERE
appid = #{appid}
and uid = #{uid}

mapper.select:
SELECT
count(1)
from
tb_org_application
where
cid = #{cid}

    and apptype = #{apptype}


    and devtype = #{devtype}
