mapper.update:
update tb_application_verify
set status = #{status},
    upduid=#{uid},
    updtime=#{updtime}
where appid=#{appid} and appvsn=#{appvsn} and status = '1';

update tb_platform_helper
set updtime=#{time},upduid=#{uid}
,title=#{title}
,context=#{context}
,status=#{status}
where helpid=#{helpid};

update tb_open_api_type
set upduid=#{uid},
updtime=#{time}
,cname = #{cname}
,status=#{status}
where otid = #{otid}

update tb_open_api_list
set upduid=#{uid},updtime =#{time}
,cname=#{cname}
,curl=#{curl}
,apidesc=#{apidesc}
,reqtype=#{reqtype}
,apibody=#{apibody}
,status=#{status}
where apiid=#{apiid};

mapper.select:
select oa.cid,ad.uid from tb_app_developer ad left join tb_org_application oa on oa.appid = ad.appid where 1=1
and oa.cid = #{cid}
and ad.uid in (SELECT regexp_split_to_table( #{uids},',' ) )
order by cid,uid LIMIT #{pagesize} OFFSET #{start}

select count(1) from tb_app_developer ad left join tb_org_application oa on oa.appid = ad.appid where 1=1
and oa.cid = #{cid}
and ad.uid in (SELECT regexp_split_to_table( #{uids},',' ) )

SELECT distinct av.cid, av.appid, av.appname, av.appvsn, av.showvsn, av.apptype, av.appurl, av.status, ad.uid FROM tb_application_verify av LEFT JOIN tb_app_developer ad ON ad.appid = av.appid and ad.devauth = '1' WHERE av.appname ~ #{value}
or av.cid in (SELECT regexp_split_to_table(#{cids}, ','))
order by av.cid, av.showvsn LIMIT #{pagesize} OFFSET #{start}

select count (1) from ( SELECT distinct av.cid, av.appid, av.appname, av.appvsn, av.showvsn, av.apptype, av.appurl, av.status, ad.uid FROM tb_application_verify av LEFT JOIN tb_app_developer ad ON ad.appid = av.appid and ad.devauth = '1' WHERE av.appname ~ #{value}
or av.cid in (SELECT regexp_split_to_table(#{cids}, ','))
) as aaa

select *
from tb_application_verify where
 appid=#{appid}
and appvsn=#{appvsn};

select helpid, title, context, to_char(to_timestamp(addtime / 1000), 'yyyy-MM-dd') as addtime
from tb_platform_helper
where status = '1' and title ~ #{value}
order by helpid  LIMIT #{pagesize}  OFFSET #{start}

select count(1)
from tb_platform_helper
where status = '1' and title ~ #{value}

SELECT distinct toal.apiid, toat.cname AS TYPE, toal.cname, to_char( to_timestamp( toal.addtime / 1000 ), 'yyyy-MM-dd' ) AS uploadtime,toal.idx FROM tb_open_api_list toal LEFT JOIN tb_open_api_type toat ON toat.otid = toal.otid WHERE toal.status = '1'
AND toal.addtime >= #{start}
AND #{end} >= toal.addtime
AND toal.cname ~ #{value}
AND toal.otid = #{otid}
order by toal.idx LIMIT #{pagesize} OFFSET #{start}

SELECT distinct count(toal.apiid)
FROM
tb_open_api_list toal
LEFT JOIN tb_open_api_type toat ON toat.otid = toal.otid
WHERE
toal.status = '1'

    AND toal.addtime >= #{starttime}


    AND #{endtime} >= toal.addtime


    AND toal.cname ~ #{value}


    AND toal.otid = #{otid}
