mapper.select:
select * from tb_score_rule  where status = '1'
and ruleid = #{ruleid}
and cname = #{cname}
and optid =  #{optid}
and score = #{score}
and utype = #{utype}
and addtime = #{addtime}
and updtime = #{updtime}
limit #{rows}  offset #{page}

select * from tb_score_list where  1 = 1
and cid = #{cid}
and pid = #{pid}
and uid =  #{uid}
and optid = #{optid}
and opttime = #{opttime}
and score = #{score}
limit #{rows}  offset #{page}

select count(1) from tb_score_rule  where status = '1'
and ruleid = #{ruleid}
and cname = #{cname}
and optid =  #{optid}
and score = #{score}
and utype = #{utype}
and addtime = #{addtime}
and updtime = #{updtime}

select count(1) from tb_score_list where 1 = 1
and cid = #{cid}
and pid = #{pid}
and uid =  #{uid}
and optid = #{optid}
and opttime = #{opttime}
and score = #{score}

mapper.update:
 update tb_score_rule set updtime  = #{updtime}
, ruleid  =  #{ruleid}
, cname  = #{cname}
, optid  = #{optid}
, score  = #{score}
, utype  = #{utype}
where id = #{id}

update tb_score_list set opttime = #{opttime}
, cid  =  #{ruleid}
, pid  = #{pid}
, optid  = #{optid}
, score  = #{score}
, uid  = #{uid}
where id =  #{id}
