mapper.select:
select  id , travelid , apid , idx , truid , trtime , trcomment , trstatus , addtime , adduid , status , summary , version  from tb_travel_approval

   and id = #{id} 
   and travelid = #{travelid} 
   and apid = #{apid} 
   and idx = #{idx} 
   and truid = #{truid} 
   and trtime = #{trtime} 
   and trcomment = #{trcomment} 
   and trstatus = #{trstatus} 
   and addtime = #{addtime} 
   and adduid = #{adduid} 
   and status = #{status} 
   and summary = #{summary} 
   and version = #{version} 


	limit #{limit} offset #{offset}

select count(*) from tb_travel_approval

  		   and id = #{id} 
  		   and travelid = #{travelid} 
  		   and apid = #{apid} 
  		   and idx = #{idx} 
  		   and truid = #{truid} 
  		   and trtime = #{trtime} 
  		   and trcomment = #{trcomment} 
  		   and trstatus = #{trstatus} 
  		   and addtime = #{addtime} 
  		   and adduid = #{adduid} 
  		   and status = #{status} 
  		   and summary = #{summary} 
  		   and version = #{version}

mapper.update:
update tb_travel_approval
travelid = #{travelid},
idx = #{idx}, truid = #{truid}, trtime = #{trtime}, trcomment = #{trcomment}, trstatus = #{trstatus}, addtime = #{addtime}, adduid = #{adduid}, status = #{status}, summary = #{summary}, version = #{version}
where apid = #{apid}
