mapper.select:
SELECT travelid,uname,fare,expenses,orgtime,endtime,trip,addtime,adduid,aptype,ccuid,"sum",rebversion,rebstatus
from tb_travel_record where  aptype = '2' and  (rebstatus='1'or rebstatus='2')
and uid=#{uid} 
and travelid = #{travelid} 
and orgtime >= #{orgtime} 
and endtime <= #{endtime} 
 limit #{pagesize}  offset #{pageno}

SELECT count(1)
from tb_travel_record where rebstatus='1' and uid=#{uid} and aptype = '2'
and travelid = #{travelid} 
and orgtime >= #{orgtime} 
and endtime <= #{endtime}

SELECT count(1) from tb_travel_record_deta where status='1'
and uid = #{uid}
and cid = #{cid}
and travelid = #{travelid}
and deid = #{deid}
and orgtime >= #{orgtime} 
and endtime <= #{endtime}

select count(1) from  tb_travel_info where deid=#{deid}
and cid=#{cid}
and projectid=#{projectid}

select  apid,trstatus,truid,status,version,idx from tb_travel_approval where  travelid = #{travelid}
and trstatus = #{trstatus}
and version = #{rebversion}
order by idx asc
