mapper.select:
SELECT count(1)
from tb_travel_record where  aptype = '2' and (rebversion != null or rebversion !='') and rebstatus !='4'
and uid=#{uid} 
and travelid = #{travelid} 
and orgtime >= #{orgtime} 
and endtime <= #{endtime}

SELECT count(1) from tb_travel_record_deta where status='1'
and uid = #{uid}
and cid = #{cid}
and travelid = #{travelid}
and deid = #{deid}
and orgtime >= #{orgtime} 
and endtime <= #{endtime}

select count(1) from  tb_travel_info where status = '1' and deid=#{deid}
and cid=#{cid}
and projectid=#{projectid}

mapper.update:
update tb_travel_info
orgrelation = #{orgrelation}, supervise = #{supervise}, prodesc = #{prodesc}, prolist = #{prolist}, projectid = #{projectid}, projectname = #{projectname}, tendercompany = #{tendercompany}, tendername = #{tendername}, projecttype = #{projecttype}, promail = #{promail}, projectras = #{projectras}, tenderphone = #{tenderphone}, tenderresultcompany = #{tenderresultcompany},
tendertime = #{tendertime}, tendertime = null
tenderagent = #{tenderagent}, relation = #{relation}, idx = #{idx}, podeperson = #{podeperson}, podephone = #{podephone}, podejob = #{podejob}, podenature = #{podenature}, npodeperson = #{npodeperson}, npodephone = #{npodephone}, npodejob = #{npodejob}, npodenature = #{npodenature}, setb = #{setb}, sepr = #{sepr}, seli = #{seli}, recname = #{recname}, reluname = #{reluname}, relphone = #{relphone}, brandname = #{brandname}, contrast = #{contrast}, mail = #{mail}, nreluname = #{nreluname}, nrelphone = #{nrelphone}, nbrandname = #{nbrandname}, ncontrast = #{ncontrast}, nmail = #{nmail}, reshid = #{reshid}, reshname = #{reshname}, reshphone = #{reshphone}, lineuname = #{lineuname}, linejob = #{linejob}, linephone = #{linephone}, linenature = #{linenature}, adduid = #{adduid}, addtime = #{addtime}, updtime = #{updtime}, status = #{status}, summary = #{summary}, upduid = #{upduid}
where status ='1' and cid = #{cid} and projectid = #{projectid}
