mapper.select:
select * from tb_user_score where status = '1'
and recharge = #{recharge}
and consume = #{consume}
and sumscore =  #{sumscore}
and freezes = #{freezes}
and score = #{score}
and uid = #{uid}
limit #{rows}  offset #{page}

select * from tb_user_month_score where 1 = 1
and month = #{month}
and consume = #{consume}
and integrals =  #{integrals}
and consume = #{consume}
and score = #{score}
and uid = #{uid}
and otime = #{otime}
limit #{rows}  offset #{page}

select count(1) from tb_user_score where status = '1'
and recharge = #{recharge}
and consume = #{consume}
and sumscore =  #{sumscore}
and freezes = #{freezes}
and score = #{score}
and uid = #{uid}
and updtime = #{updtime}

select count(1) from tb_user_month_score where 1 = 1
and month = #{month}
and consume = #{consume}
and integrals =  #{integrals}
and consume = #{consume}
and score = #{score}
and uid = #{uid}
and otime = #{otime}

mapper.update:
update tb_user_score set  updtime = #{updtime}
,sumscore  = #{sumscore}
,consume  = #{consume}
,recharge  = #{recharge}
,freezes  = #{freezes}
,score  = #{score}
where  id = #{id}

update tb_user_month_score set otime = #{otime}
,sumscore  = #{sumscore}
,consume  = #{consume}
,recharge  = #{recharge}
,freezes  = #{freezes}
,score  = #{score}
where  id = #{id}
