                    and addtime <= #{twelve}

                ),
            ( SELECT COUNT ( 1 ) AS deepconnectcount FROM tb_track_user_list WHERE trackres = '2' AND status = '1'

                    and trackuid = #{trackuid}


                    and addtime >= #{item.starttime}


                    and addtime <= #{item.endtime}


                    and trackuid in (
