mapper.update:
update
tb_company_list
SET
region = #{region}
WHERE
cid=#{cid}

UPDATE
tb_company_list
SET
manager = #{manager}
WHERE
cid = #{cid}

mapper.select:
SELECT
count(1)
from
tb_invite_user
where
cid = #{cid}
and
status = #{status}
and
reqtype = #{reqtype}

SELECT
resuid
from
tb_invite_user
where
cid = #{cid}
and
status = #{status}
and
reqtype = #{reqtype}
order by
reqtime desc
limit
#{pagesize}
offset
#{pageno}

mapper.insert:
INSERT INTO tb_dept_user (cname, phone, idx, cid, cdid, uid, addtime, status, summary) VALUES ( #{cname}, #{relphone}, (select count(1)+1 from tb_dept_user), #{cid},
#{cdid},
CONCAT( #{cid},'0000'),
#{uid}, #{restime},
#{status}
'1'
,#{summary}
,null
)

mapper.update:
UPDATE
tb_dept_user
SET
status = '1',
updtime = #{restime}
WHERE
uid = #{resuid}
and
cid = #{cid}
and
cdid = #{cdid}

mapper.select:
SELECT uid, cid, cdid from tb_dept_user where
uid = #{item.uid} and cid = #{item.cid}
and cdid = #{item.cdid}
and cdid = CONCAT( #{item.cid},'0000')
and (status = '1' or status = '2')

update.foreach:
UPDATE
tb_dept_user
SET
status = '1',
updtime = #{item.restime}
WHERE
cid = #{item.cid}
and
uid = #{item.uid}
and
cdid = #{item.cdid}

mapper.insert:
INSERT INTO tb_dept_user (cname, phone, idx, cid, cdid, uid, addtime, status, summary) VALUES
( #{item.cname}, #{item.relphone}, (select count(1)+1 from tb_dept_user), #{item.cid},
#{item.cdid},
CONCAT( #{item.cid},'0000'),
#{item.uid}, #{item.restime},
#{item.status}
'1'
,#{item.summary}
,null
)

mapper.select:
SELECT
count(1)
from
tb_dept_user
where
uid = #{resuid}
and cid = #{cid}
and cdid = #{cdid}
and
(status = '1' or status = '2')

SELECT du.uid, string_agg ( DISTINCT cl.joname, ',' ) AS joname, string_agg ( DISTINCT reg.regname, ',' ) AS regname, string_agg ( DISTINCT du.cdid, ',' ) AS cdid FROM ( SELECT cid, cname || '-' || CASE COALESCE ( authid, 0 ) WHEN 0 THEN '未认证' ELSE'已认证' END AS joname FROM tb_company_list ) cl JOIN tb_dept_user du ON du.cid = cl.cid AND du.status = '1' LEFT JOIN ( SELECT reguid, cname || '-' || CASE COALESCE ( authid, 0 ) WHEN 0 THEN '未认证' ELSE'已认证' END AS regname FROM tb_company_list WHERE reguid IS NOT NULL ) reg ON reg.reguid = du.uid WHERE du.uid IN
#{item.uid}
GROUP BY du.uid

