mapper.select:
SELECT DISTINCT aaa.* FROM ( SELECT distinct cl.id, cl.cid, cl.cname, cl.region, du.cname AS uname, du.phone, cs.systemname, ci.address, to_char(to_timestamp(cl.regtime / 1000), 'yyyy-MM-dd') as jointime, cl.authid, COALESCE ( au.ispc, 2 ) AS ispc, COALESCE ( au.isapp, 2 ) AS isapp FROM tb_company_list cl LEFT JOIN tb_dept_user du ON du.cid = cl.cid AND du.uid = cl.manager LEFT join tb_company_sysconfig cs on cs.cid = cl.cid left join tb_company_info ci on ci.cid = cl.cid LEFT JOIN ( SELECT prod.cid, MIN ( prod.ispc ) AS ispc, MIN ( prod.isapp ) AS isapp FROM ( SELECT sq.cid, CASE sq.apptype WHEN 1 THEN 1 ELSE 2 END AS ispc, CASE sq.apptype WHEN 2 THEN 1 ELSE 2 END AS isapp FROM ( SELECT DISTINCT cid, apptype FROM tb_product_list WHERE status = '1' ) sq ) prod GROUP BY prod.cid ) au ON au.cid = cl.cid where cl.status = #{status} and cl.authid = ${authid} and cl.authid > ${authid}
and (cl.cname LIKE CONCAT( '%',#{cname},'%') or du.cname LIKE CONCAT( '%',#{cname},'%'))
) aaa order by aaa.id desc limit #{pagesize} offset #{pageno}

mapper.insert:
INSERT INTO tb_company_common (cid, idx, ouid, addtime, updtime, status, summary) VALUES ( #{cid}, (select count(1)+1 from tb_company_common), #{uid}, #{addtime}, #{updtime}, #{status}
,#{summary}
,null
)

mapper.select:
SELECT count(1) FROM tb_company_common cc LEFT JOIN tb_company_list cl on cc.cid = cl.cid LEFT JOIN tb_dept_user du ON du.cid = cl.cid AND du.uid = cl.manager left join tb_company_info ci on ci.cid = cl.cid where cc.status = #{status}
and cl.region IN
#{item.region}
and (cl.cname LIKE CONCAT( '%',#{cname},'%') or du.cname LIKE CONCAT( '%',#{cname},'%'))

SELECT cc.cid, cl.cname, cl.region, du.cname AS uname, du.phone, ci.address, cl.authid, prolist, tccl.prodesc, tccl.orgrelation, tccl.supervise FROM tb_company_common cc LEFT JOIN tb_company_list cl on cc.cid = cl.cid LEFT JOIN tb_dept_user du ON du.cid = cl.cid AND du.uid = cl.manager left join tb_company_info ci on ci.cid = cl.cid LEFT JOIN tb_common_company_list tccl on cc.cid = tccl.cid where cl.status = #{status} and cc.status = #{status}
and cl.region IN
#{item.region}
and (cl.cname LIKE CONCAT( '%',#{cname},'%') or du.cname LIKE CONCAT( '%',#{cname},'%'))
order by cc.id desc limit ${pagesize} offset ${pageno}

SELECT cid, projectid, projectname, region, address, plantime, verifytime, tendertime, tenderuid, tendername, tenderphone, wintime, buildstime, buildetime, to_char(to_timestamp(addtime / 1000), 'yyyy-MM-dd HH24:MI') as addtime, summary FROM tb_company_project where status = #{status} and cid = #{cid}
and projectname LIKE CONCAT( '%',#{projectname},'%')
order by id desc limit #{pagesize} offset #{pageno}

