mapper.select:
select trim(cid) as cid,
cname
from tb_common_company_list where  cid=#{cid}

mapper.update:
update tb_common_company_list
supervise=#{supervise}, orgrelation=#{orgrelation}, prodesc=#{prodesc}, prolist=#{prolist}, upduid=#{uid}, updtime=#{updtime}
cid=#{cid}

update tb_company_project
tendername=#{tendername}, tenderphone=#{tenderphone},
tendertime = #{tendertime},
tendertime = null,
tendercompany=#{tendercompany}, projecttype=#{projecttype}, mail=#{promail}, tenderresultcompany=#{tenderresultcompany}, tenderagent=#{tenderagent}, agentphone=#{agentphone}, porjcetra=#{porjcetra}, relation=#{relation}, upduid=#{uid}, updtime=#{updtime}
projectid=#{projectid}

update tb_common_project_info
lineuname=#{ lineuname}, linephone=#{ linephone}, linejob=#{ linejob}, linenature=#{ linenature}, des=#{ des}, idx=#{ idx}, podeperson=#{ podeperson}, podephone=#{ podephone}, podejob=#{ podejob}, podenature=#{ podenature}, npodeperson=#{ npodeperson}, npodephone=#{ npodephone}, npodejob=#{ npodejob}, npodenature=#{ npodenature}, setb=#{ setb}, sepr=#{ sepr}, setli=#{ setli}, relation=#{ relation}, recname=#{ recname}, reluname=#{ reluname}, relphone=#{ relphone}, brandname=#{ brandname}, contrast=#{ contrast}, mail=#{ mail}, nreluname=#{ nreluname}, nrelphone=#{ nrelphone}, nbrandname=#{ nbrandname}, ncontrast=#{ ncontrast}, nmail=#{ nmail}, reshid=#{ reshid}, reshname=#{ reshname}, reshphone=#{ reshphone}, addtime=#{ addtime}, upduid=#{ uid}, updtime=#{ updtime}
projectid=#{projectid}
