mapper.select:
select trackcontent ,abdialogue,nextadvance from tb_track_user_list where status='1'
and  customer = #{uid}
and trackvalue = #{cid}
and trackproid = #{projectid}
and tracktime between #{begDate} and #{endDate} 
limit #{pagesize}  offset #{pageno}

