mapper.select:
select * from ( SELECT DISTINCT tcc.cid, tcc.relmail, tcc.relperson, tcc.orgrelation, tcc.prolist, tcc.supervise, tcc.relphone, tcc.prodesc, tcc.region, tpcl.oneheader, tpcl.twoheader, tpcl.threeheader, tpcl.fourheader, tpcl.fiveheader, tpcl.sixheader, tpcl.status, (select cname from tb_dept_user where uid=#{uid} limit 1) sales, tcc.status, tcl.cname, tcl.manager, tcl.region, tdu.cname username, tdu.phone userphone, ( SELECT dictvalue FROM tb_company_property WHERE cid = tcc.cid AND typeid = 't111111' limit 1) companyLevel, tcl.authid, tcl.logo, tcl.relmail, tci.address FROM tb_common_company_list tcc INNER JOIN tb_company_list tcl ON tcl.cid = tcc.cid LEFT JOIN tb_private_company_list tpcl ON tcc.cid = tpcl.cid LEFT JOIN tb_dept_user tdu ON tdu.cid = tcc.cid AND tdu.uid = tcl.manager LEFT JOIN tb_company_property tcp ON tcp.cid = tcc.cid LEFT JOIN tb_company_info tci ON tcl.cid = tci.cid WHERE tcc.status = '2' and tpcl.status = '1' ) a where 1=1
and (a.oneheader = #{uid} or a.twoheader = #{uid} or a.threeheader = #{uid} or a.fourheader = #{uid} or a.fiveheader = #{uid} or a.sixheader = #{uid})
and a.region IN
#{item.region}
limit #{pagesize} offset #{pageno}

