mapper.select:
select *from ( select * from ( SELECT DISTINCT tcc.oneheader, tcc.cid, (select cname from tb_dept_user where uid=tcc.oneheader limit 1) sales, tcc.status, tcl.cname, tcl.manager, tcl.region, tdu.phone userphone, ( SELECT dictvalue FROM tb_company_property WHERE cid = tcc.cid AND typeid = 't111111' limit 1) dictvalue, tcl.authid, tcl.logo, tcl.relmail, tcd.duid FROM tb_company_dept tcd LEFT JOIN tb_dept_user tdu ON tcd.cdid = tdu.cdid LEFT JOIN tb_private_company_list tcc ON tcc.oneheader = tdu.uid LEFT JOIN tb_company_list tcl ON tcl.cid = tcc.cid LEFT JOIN tb_company_property tcp ON tcp.cid = tcc.cid WHERE tcc.status != '4' ) a where 1=1 and a.duid = #{uid} and a.status = '1'
and a.region=#{region}
and a.dictvalue=#{dictvalue}
and (a.cname ~ #{keyword} or a.sales ~ #{keyword})
and a.region IN
#{item.region}
UNION select * from ( SELECT DISTINCT tcc.oneheader, tcc.cid, (select cname from tb_dept_user where uid=tcc.oneheader limit 1) sales, tcc.status, tcl.cname, tcl.manager, tcl.region, tdu.phone userphone, ( SELECT dictvalue FROM tb_company_property WHERE cid = tcc.cid AND typeid = 't111111' limit 1) dictvalue, tcl.authid, tcl.logo, tcl.relmail, '' as duid FROM tb_private_company_list tcc LEFT JOIN tb_company_list tcl ON tcl.cid = tcc.cid LEFT JOIN tb_dept_user tdu ON tdu.cid = tcc.cid LEFT JOIN tb_company_property tcp ON tcp.cid = tcc.cid WHERE tcc.status != '4' ) a where 1=1 and a.oneheader = #{uid} and a.status = '1'
and a.region=#{region}
and a.dictvalue=#{dictvalue}
and (a.cname ~ #{keyword} or a.sales ~ #{keyword})
and a.region IN
#{item.region}
) as b limit #{pagesize} offset #{pageno}

select count(1) from ( select *from ( select * from ( SELECT DISTINCT tcc.oneheader, tcc.cid, (select cname from tb_dept_user where uid=tcc.oneheader limit 1) sales, tcc.status, tcl.cname, tcl.manager, tcl.region, tdu.phone userphone, ( SELECT dictvalue FROM tb_company_property WHERE cid = tcc.cid AND typeid = 't111111' limit 1) dictvalue, tcl.authid, tcl.logo, tcl.relmail, tcd.duid FROM tb_company_dept tcd LEFT JOIN tb_dept_user tdu ON tcd.cdid = tdu.cdid LEFT JOIN tb_private_company_list tcc ON tcc.oneheader = tdu.uid LEFT JOIN tb_company_list tcl ON tcl.cid = tcc.cid LEFT JOIN tb_company_property tcp ON tcp.cid = tcc.cid WHERE tcc.status != '4' ) a where 1=1 and a.duid = #{uid} and a.status = '1'
and a.region=#{region}
and a.dictvalue=#{dictvalue}
and (a.cname ~ #{keyword} or a.sales ~ #{keyword})
and a.region IN
#{item.region}
UNION select * from ( SELECT DISTINCT tcc.oneheader, tcc.cid, (select cname from tb_dept_user where uid=tcc.oneheader limit 1) sales, tcc.status, tcl.cname, tcl.manager, tcl.region, tdu.phone userphone, ( SELECT dictvalue FROM tb_company_property WHERE cid = tcc.cid AND typeid = 't111111' limit 1) dictvalue, tcl.authid, tcl.logo, tcl.relmail, '' as duid FROM tb_private_company_list tcc LEFT JOIN tb_company_list tcl ON tcl.cid = tcc.cid LEFT JOIN tb_dept_user tdu ON tdu.cid = tcc.cid LEFT JOIN tb_company_property tcp ON tcp.cid = tcc.cid WHERE tcc.status != '4' ) a where 1=1 and a.oneheader = #{uid} and a.status = '1'
and a.region=#{region}
and a.dictvalue=#{dictvalue}
and (a.cname ~ #{keyword} or a.sales ~ #{keyword})
and a.region IN
#{item.region}
) as b ) as num
