mapper.select:
select num.cname, num.relperson, num.dictvalue, num.projectname, num.contamount, num.ac, num.checkamount from ( select a.cname as cname, a.cid as ac, b.cid as bc, /*业主名称*/ c.cid as cc, /*业主名称*/ d.cid as dc, /*业主名称*/ e.cid as ec, a.relperson, /*联系人*/ b.dictvalue, /*客户等级*/ c.projectname, /*项目名称*/ d.contamount, d.checkamount /*预计合同金额*/ from tb_company_list a Inner JOIN tb_company_property b on a.cid = b.cid LEFT JOIN tb_company_project c on c.cid = a.cid and c.cid = b.cid LEFT JOIN tb_company_contract d on d.cid = a.cid and d.cid = c.cid left join tb_company_common e on e.cid = d.cid and e.cid = c.cid where b.typeid = 't111111'
and a.cname ~ #{keyword} or e.uid ~ #{keyword}
and b.dictvalue = #{dictvalue}
and e.uid = #{uid}
) as num where ac is not null and bc is not null and cc is not null and dc is not null and ec is not null limit #{pagesize} offset #{pageno}

select count(1) from (select num.cname, num.relperson, num.dictvalue, num.projectname, num.contamount, num.ac, num.checkamount from (select a.cname, a.cid as ac, b.cid as bc, /*业主名称*/ c.cid as cc, /*业主名称*/ d.cid as dc, /*业主名称*/ e.cid as ec, a.relperson, /*联系人*/ b.dictvalue, /*客户等级*/ c.projectname, /*项目名称*/ d.contamount, /*预计合同金额*/ d.checkamount from tb_company_list a Inner JOIN tb_company_property b on a.cid = b.cid LEFT JOIN tb_company_project c on c.cid = a.cid and c.cid = b.cid LEFT JOIN tb_company_contract d on d.cid = a.cid and d.cid = c.cid left join tb_company_common e on e.cid = d.cid and e.cid = c.cid where b.typeid = 't111111'
and a.cname ~ #{keyword} or e.uid ~ #{keyword}
and b.dictvalue = #{dictvalue}
and e.uid = #{uid}
) as num where ac is not null and bc is not null and cc is not null and dc is not null and ec is not null ) as num

SELECT * FROM ( SELECT DISTINCT tcl.cid, tcl.cname, tcp1.projectname, (SELECT dictvalue FROM tb_company_property WHERE cid = tcl.cid AND typeid = 't111111' limit 1) dictvalue, tcc.contamount FROM tb_company_list tcl LEFT JOIN tb_company_property tcp ON tcl.cid = tcp.cid LEFT JOIN tb_company_contract tcc ON tcc.cid = tcl.cid LEFT JOIN tb_company_project tcp1 ON tcp1.cid = tcl.cid ) as a where 1 = 1
and a.dictvalue = #{dictvalue}
and a.projectname like concat('%',#{cname},'%')
and a.cid = #{cid}
