mapper.select:
WITH RECURSIVE item AS ( ( SELECT planid, cdid, uid, dutyid, plancontent, starttime, endtime, acceptors, executors, assistants, outcome, tasktype, taskstatus , notes, path, filenames, addid, review, parentid, sort FROM ${tabname} where parentid= '0' and node=#{node}
and cdid= #{cdid}
and starttime >= #{starttime}
and endtime <= #{endtime}
and tasktype <= #{tasktype}
and taskstatus = #{taskstatus}
and acceptors like '%'#{acceptors}'%'
and executors like '%'#{executors}'%'
and assistants like '%'#{assistants}'%'
and outcome > #{outcome}
and uid = #{uid}
LIMIT #{pagesize} OFFSET #{pageno} ) UNION all SELECT itema.planid, itema.cdid, itema.uid, itema.dutyid, itema.plancontent, itema.starttime, itema.endtime, itema.acceptors, itema.executors, itema.assistants, itema.outcome, itema.tasktype, itema.taskstatus , itema.notes, itema.path, itema.filenames, itema.addid, itema.review, itema.parentid, itema.sort FROM ${tabname} itema JOIN item ON itema.parentid = item.planid and itema.node = #{node} ) SELECT item.* FROM item
ORDER BY item.sort asc

	SELECT
		count(1)
	FROM
		${tabname}
    where node=#{node} and parentid= '0'

			and starttime >= #{starttime}


			and endtime  <= #{endtime}


			and tasktype  <= #{tasktype}



			and taskstatus  = #{taskstatus}


			and acceptors  like '%'#{acceptors}'%'



			and executors  like '%'#{executors}'%'



			and assistants  like '%'#{assistants}'%'



			and outcome  > #{outcome}


			and uid = #{uid}
