mapper.update:
update "tb_document_msg_file_info"


        name = #{name},


        description=#{description},


        uid=#{uid},


        uptime=#{uptime}


where uniquefileid=#{uniquefileid}

mapper.select:
select id,uniquefileid as identifier,name as filename,type,path as location,addtime,uid,description,uptime,status,sort,href,totalsize as totalSize from "tb_document_msg_file_info" where href like #{href}
and name like CONCAT('%',#{name},'%')
and status = '1'

update.foreach:
update tb_document_msg_file_info
set status = '2'
where uniquefileid = #{item.uniquefileid}

update tb_document_msg_file_info


        path=#{item.path},


        href=#{item.href}


where uniquefileid = #{item.uniquefileid}

mapper.update:
update tb_document_msg_file_info


        name=#{name},


        path=#{newPath},


        href=#{newHref},


        description=#{description}



where uniquefileid = #{uniquefileid}
